import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Customer } from 'src/app/model/customer';
import { CustomerService } from 'src/app/services/customer.service';

@Component({
  selector: 'app-customer',
  templateUrl: './customer.component.html',
  styleUrls: ['./customer.component.css']
})
export class CustomerComponent {
  public customers : Customer[];
  public editCustomer : Customer;
  public deleteCustomer : Customer;
  public stats : {id : number, firstName: string,lastName: string, likes  : number }[];
  public currentPage : number = 1;
  public totalPages : number = 0;

  constructor(private customerService :CustomerService, private router : Router, private activatedRoute : ActivatedRoute) {}

  ngOnInit() : void {
    this.getCustomers();
  }

  public getCustomers() : void {
    this.customerService.getAllCustomers(this.currentPage, 10).subscribe(
      response => {
        this.customers = response.content;
        this.totalPages = response.totalPages;
      }
    );
  }

  public onOpenModal(customer : Customer, mode : string) : void {

  }
}
